package com.mib.service2.service;

import java.nio.charset.StandardCharsets;
import java.time.LocalDate;
import java.util.Base64;
import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.atomic.AtomicInteger;

import org.springframework.stereotype.Service;

import com.mib.service2.dto.DocumentoResponse;

@Service
public class DocumentoService {

    private static final int CANT_DOC = 20;
    private final AtomicInteger contador = new AtomicInteger(0);

    public DocumentoResponse generarDocumento() {

        int ndoc = contador.incrementAndGet();
        if (ndoc > CANT_DOC) {
            contador.set(1);
            ndoc = 1;
        }

        DocumentoResponse response = new DocumentoResponse();
        response.ndoc = ndoc;
        response.cantdoc = CANT_DOC;
        response.documento = generarPdfBase64();
        response.metadata = generarMetadata();
        response.cargo = "Director";
        response.razon = "Firma de documento";
        response.localidad = "Buenos Aires";
        response.url_callback = "https://localhost:8445/api/documentosFirmados";

        return response;
    }

    private String generarPdfBase64() {
        String documento = "";
        return documento;
    }

    private Map<String, String> generarMetadata() {
        Map<String, String> metadata = new HashMap<>();
        metadata.put("fecha", LocalDate.now().toString());
        metadata.put("organismo", "economia");
        return metadata;
    }
}
